mapper.select:
SELECT rid, name, endpoint, sight, number FROM route



              or rid =#{rid}



             or name like '%'#{name}'%'



              or endPoint like '%'#{endPoint}'%'



              like sight = '%'#{sight}'%'



              or number  = #{number}

mapper.update:
UPDATE route
SET rid=#{rid}, name=#{name}, endpoint=#{endPoint}, sight=#{sight}, "number"=#{number}
WHERE rid = #{rid}
