mapper.update:
UPDATE vehicle
SET vid=#{vid}, rid=#{rid}, state=#{state}, id=#{id}, pnumber=#{pnumber}
WHERE vid = #{vid}

UPDATE vehicle
     SET  state = #{state}
     WHERE  vid = #{vid}

UPDATE vehicle
     SET  rid = #{rid}
     WHERE  vid = #{vid}

UPDATE vehicle
     SET  pumber=#{pnumber}
     WHERE  vid = #{vid}
